import React, {useState, useReducer} from 'react';
import {View, Text, Button, Alert, TextInput, ScrollView, StyleSheet, Platform, TouchableOpacity} from 'react-native';
import DateTimePicker from '@react-native-community/datetimepicker';
import moment from 'moment'
import DatePickerModal from "../components/DatePickerModal";
import ImagePicker from "../components/ImagePicker";
import Colors from "../constants/Colors";
import { useDispatch } from "react-redux";
import { addExpense } from '../store/expenses-actions'
import InputText from '../components/InputText'


function reducer(state, action) {
    let updatedValues;
    let updateTouched;
    switch (action.type) {
        case 'setText':
            updatedValues = {
                ...state.values,
                [action.param]: action.value
            }
            return {
                ...state,
                values: updatedValues
            }
        case 'setErrors':
            updatedValues = state.errors.filter(error => error.param !== action.param)
            updatedValues = [...updatedValues, ...action.errors]
            return {
                ...state,
                errors: updatedValues
            }
        case 'setDate':

            const updatedState = {
                ...state
            }
            updatedState.values.date = action.value
            if(action.android) {
                updatedState.datePick = false
            }
            return {
                ...state,
                state: updatedState,
                [state.touched.date]: true,
            }
        case 'addImg':
            updatedValues = {
                ...state.values,
                image: action.value
            }
            updateTouched = {
                ...state.touched,
                image: true,
            }
            return {
                ...state,
                values: updatedValues,
                touched: updateTouched
            }
        case 'openDatePick':
            return {
                ...state,
                datePick: true
            }
        case 'closeDatePick':
            return {
                ...state,
                datePick: false
            }
        case 'setTouched':
            updatedValues = {
                ...state.touched,
                [action.param]: true
            }
            return {
                ...state,
                touched: updatedValues
            }
    }
}

const AddEditExpenses = ({ navigation }) => {

    const editExpense = false

    const [state, dispatch] = useReducer(reducer, {
        values: {
            supplier: editExpense ? editExpense.supplier : '',
            amount: editExpense ? editExpense.amount : '',
            date: editExpense ? editExpense.date : new Date(),
            image: editExpense ? editExpense.image : null,
        },
        touched: {
            supplier: false,
            amount: false,
            image: false,
        },
        datePick: false,
        errors: [{
            param: 'amount',
            msg: 'Please this is an error'
        }]
    });

    const reduxDispatch = useDispatch()

    const addExpenseHandler = ({ supplier, amount, date, image }) => {

        const notTouched = Object.keys(state.touched).filter(item => state.touched[item] === false && item)

        if(notTouched.length > 0) {
            notTouched.forEach(param => {
                const msg = param === 'image' ? "Please upload an image" : "Please enter a value"
                const errors = [
                    {param, msg}
                ]
                dispatch({type: 'setErrors', param, errors})
                dispatch({type: 'setTouched', param})
            })
        } else {
            const success = reduxDispatch(addExpense(supplier, amount, date, image))
            if(success) {
                navigation.goBack()
            }
        }
    }

    const imageTakenHandler = imagePath => {
        dispatch({type: 'setErrors', param: 'image', errors: []})
        dispatch({type: 'addImg', value: imagePath})
    }

    const valueUpdateHandler = (text, param, errorArray) => {
        dispatch({type: 'setTouched', param})
        dispatch({type: 'setText', value: text, param})
        dispatch({type: 'setErrors', param, errors: errorArray})
    }

    return (
        <ScrollView contentContainerStyle={{ width: '100%', flex: 1, alignItems: 'center' }}>
            {Platform.OS === 'ios' && <DatePickerModal visibility={state.datePick} dispatch={dispatch} date={state.values.date}/>}
            <View style={styles.form}>
                <InputText
                    title="Supplier Name"
                    value={state.values.supplier}
                    param="supplier"
                    spellcheck={false}
                    valueUpdate={valueUpdateHandler}
                    errors={state.errors.filter(error => error.param === 'supplier')}
                    touched={state.touched.supplier}
                />
                <InputText
                    title="Receipt Amount"
                    value={state.values.amount}
                    param="amount"
                    valueUpdate={valueUpdateHandler}
                    errors={state.errors.filter(error => error.param === 'amount')}
                    keyboardType="decimal-pad"
                    currency
                    touched={state.touched.amount}

                />
                <View style={styles.formInput}>
                    <Text style={styles.inputTitle}>Date of Receipt</Text>
                    <View style={styles.datePick}>
                        <Text>{moment(state.values.date).format('Do MMMM YYYY')}</Text>
                        <TouchableOpacity style={styles.changeBtn} onPress={() => dispatch({type: 'datePick', value: true})}>
                            <Text style={styles.changeBtnText}>Change</Text>
                        </TouchableOpacity>
                    </View>
                </View>
                {Platform.OS === 'android' && state.datePick && <DateTimePicker
                    testID="dateTimePicker"
                    value={state.values.date}
                    mode='date'
                    display="calendar"
                    onChange={(e, newDate) => {
                        console.log(e)
                        console.log(newDate)
                    }}
                    // onChange={(e, newDate) => e.type === 'set' ? dispatch({type: 'setDate', value: newDate, android: true}) : dispatch({type: 'datePick', value: false})}
                    maximumDate={new Date()}
                />}
                <ImagePicker onImageTaken={imageTakenHandler} errors={state.errors.filter(error => error.param === 'image')}/>
                <View style={styles.submitBtn}>
                    <Button title="Add Expense" onPress={() => addExpenseHandler(state.values)}/>
                </View>
            </View>
        </ScrollView>
    );
}

const styles = StyleSheet.create({
    form: {
        marginTop: 20,
        marginBottom: 20,
        width: '100%',
        flex: 1,
        alignItems: 'center'
    },
    datePick: {
        width: '100%',
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center'
    },
    submitBtn: {
        marginTop: 20,
    },
    changeBtn: {
        backgroundColor: Colors.orange,
        paddingTop: 5,
        paddingBottom: 5,
        paddingLeft: 20,
        paddingRight: 20,
        borderRadius: 5,
        shadowColor: 'black',
        shadowOpacity: .3,
        shadowRadius: 3,
        shadowOffset: {
            width: 0,
            height: 0
        }
    },
    inputTitle: {
        color: Colors.orange,
        fontSize: 15,
        width: '100%',
        textAlign: 'left'
    },
    formInput: {
        width: '90%',
        marginBottom: 15,
        alignItems: 'center'
    },
});

export default AddEditExpenses;
